import { loadRemoteModule } from '@angular-architects/module-federation';
import { Component, ComponentFactoryResolver, ComponentRef, OnInit, ViewContainerRef } from '@angular/core';

@Component({
  selector: 'app-remote-button-link',
  templateUrl: './remote-button-link.component.html',
  styleUrls: ['./remote-button-link.component.scss']
})
export class RemoteButtonLinkComponent implements OnInit {
  constructor(
    private viewContainerRef: ViewContainerRef,
    private cfr: ComponentFactoryResolver
  ){}
  async ngOnInit(){
    const {ButtonLinkComponent} = await loadRemoteModule({
      remoteEntry: 'http://localhost:4200/remoteEntry.js',
      remoteName: 'remote',
      exposedModule: './RemoteButtonLinkComponent',
    });

    const componentRef: ComponentRef<{name: string, url: string}> = this.viewContainerRef.createComponent(
      this.cfr.resolveComponentFactory(ButtonLinkComponent)
    );

    componentRef.instance.name = 'Domiiiiiiiiiiiiiiiiiiii'
    componentRef.instance.url = "https://youtube.com"
  }


}
